mapper.select:
SELECT c.id,c.category_id,c.categoryName, c.formName, date_format(c.startDate, '%Y-%m-%d') as startDate,date_format(c.endDate, '%Y-%m-%d') as endDate,date_format(c.regDate, '%Y-%m-%d') as regDate,u.userName,stateName,ifnull(state_id,0)as state_id FROM (SELECT s.*,categoryName FROM (SELECT form_id, st.user_id as user ,state_id,st.regDate as registDate,Form.*,st.stateName FROM (SELECT Result.regDate,Result.form_id,Result.user_id,State.stateName,Result.state_id FROM (SELECT * FROM Result WHERE Result.user_id=${user_id})as Result INNER JOIN State ON Result.state_id=State.id)as st RIGHT OUTER JOIN Form ON st.form_id=Form.id WHERE isUserEdit=0
)as s LEFT OUTER JOIN Category ON s.category_id=Category.id) as c INNER join User as u ON (c.user_id = u.id)
ORDER BY c.id DESC LIMIT #{pageStart}, #{perPageNum};

Select categoryName from Category 
inner join Form on Category.id = Form.category_id 
where Form.id =1;

Select categoryName from Category 
inner join Form on Category.id = Form.category_id 
where Form.id = #{id};

mapper.insert:
INSERT INTO Category ('id', 'categoryName', 'regDate') values(#{category_Id}, #{erase}, null);
INSERT INTO Category (categoryName) VALUE (#{categoryName});

    SELECT LAST_INSERT_ID();
