mapper.select:
SELECT c.id,c.category_id,c.categoryName, c.formName,url, c.startDate as startDate,c.endDate as endDate,c.regDate,u.userName,stateName,ifnull(state_id,0)as state_id FROM (SELECT s.*,categoryName FROM (SELECT form_id, st.user_id as user ,state_id,st.regDate as registDate,Form.*,st.stateName FROM (SELECT Result.regDate,Result.form_id,Result.user_id,State.stateName,Result.state_id FROM (SELECT * FROM Result WHERE Result.user_id=${user_id})as Result INNER JOIN State ON Result.state_id=State.id)as st RIGHT OUTER JOIN Form ON st.form_id=Form.id WHERE isAvailable=1
)as s LEFT OUTER JOIN Category ON s.category_id=Category.id) as c INNER join User as u ON (c.user_id = u.id)
ORDER BY c.id DESC LIMIT #{pageStart}, #{perPageNum};

		SELECT Result.id as id, User.id as user_id, userName,department,studentId,email,Result.regDate as regDate,Result.editDate as editDate, state_id,stateName 
		FROM (Select Result.*,stateName From Result INNER JOIN State ON Result.state_id=State.id)as Result 
		INNER JOIN User on Result.user_id=User.id where form_id=#{form_id} 


         AND stateName LIKE CONCAT('%', #{keyword}, '%')


		LIMIT #{pageStart}, #{perPageNum};

Select categoryName from Category 
inner join Form on Category.id = Form.category_id 
where Form.id =#{form_id};

Select categoryName from Category 
inner join Form on Category.id = Form.category_id 
where Form.id = #{id};


SELECT * FROM User WHERE isDelete =0 
AND id<>0   

LIMIT #{pageStart}, #{perPageNum};
